import { Avatar as ArkAvatar, type AvatarProps as ArkAvatarProps } from '@ark-ui/solid'
import { styled } from 'styled-system/jsx'
import { avatar, type AvatarVariantProps } from 'styled-system/recipes'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(avatar)

export type AvatarProps = ArkAvatarProps & AvatarVariantProps

const AvatarRoot = withProvider(styled(ArkAvatar.Root), 'root')
export const AvatarFallback = withContext(styled(ArkAvatar.Fallback), 'fallback')
export const AvatarImage = withContext(styled(ArkAvatar.Image), 'image')

export const Avatar = Object.assign(AvatarRoot, {
  Root: AvatarRoot,
  Fallback: AvatarFallback,
  Image: AvatarImage,
})
