import { PinInput as ArkPinInput, type PinInputProps as ArkPinInputProps } from '@ark-ui/solid'
import { styled } from 'styled-system/jsx'
import { pinInput, type PinInputVariantProps } from 'styled-system/recipes'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(pinInput)

export type PinInputProps = ArkPinInputProps & PinInputVariantProps

const PinInputRoot = withProvider(styled(ArkPinInput.Root), 'root')
export const PinInputControl = withContext(styled(ArkPinInput.Control), 'control')
export const PinInputInput = withContext(styled(ArkPinInput.Input), 'input')
export const PinInputLabel = withContext(styled(ArkPinInput.Label), 'label')

export const PinInput = Object.assign(PinInputRoot, {
  Root: PinInputRoot,
  Control: PinInputControl,
  Input: PinInputInput,
  Label: PinInputLabel,
})
